import React, {useState} from 'react';

import LoadingPage from './pages/LoadingPage';
import ResultsPage from './pages/ResultsPage';
import SearchPage from './pages/SearchPage';

import './App.css';

const sampleData = [
  {name: 'Maas Lalani', login: 'maaslalani', website: 'maaslalani.com', linkedin: 'https://linkedin.com/in/maaslalani'},
  {name: 'Darshil Patel', login: 'darshil', website: 'darshilpatel.com', linkedin: 'https://linkedin.com/in/darshil36'},
];

function Navigation() {
  return (
    <nav className="App-nav">
      <div>Git Scrape</div>
      <div>View on Github</div>
    </nav>
  );
};

function App() {
  const [search, setSearch] = useState('');
  const [results, setResults] = useState();
  const [loading, setLoading] = useState(false);

  function onSubmit() {
    setResults(sampleData);
    setLoading(true);
    setTimeout(() => setLoading(false), 3000);
  }

  const resultsPage = loading ? <LoadingPage /> : <ResultsPage results={results} />;

  return (
    <div className="App">
      {results ? resultsPage : <SearchPage search={search} setSearch={setSearch} onSubmit={onSubmit} />}
    </div>
  );
}

export default App;
