import React, {useState} from 'react';

import LoadingPage from './pages/LoadingPage';
import ResultsPage from './pages/ResultsPage';

import Search from './components/Search';
import Instructions from './components/Instructions';
import Logo from './components/Logo';
import Table from './components/Table';
import Download from './components/Download';
import './App.css';

const sampleData = [
  {name: 'Maas Lalani', login: 'maaslalani', website: 'maaslalani.com', linkedin: 'https://linkedin.com/in/maaslalani'},
  {name: 'Darshil Patel', login: 'darshil', website: 'darshilpatel.com', linkedin: 'https://linkedin.com/in/darshil36'},
];

function App() {
  const [search, setSearch] = useState('');
  const [results, setResults] = useState();
  const [loading, setLoading] = useState(false);

  function onSubmit() {
    setResults(sampleData);
    setLoading(true);
    setTimeout(() => setLoading(false), 3000);
  }

  const searchPage = (
    <div className="content">
      <Logo />
      <Instructions />
      <Search
        search={search}
        setSearch={setSearch}
        onSubmit={onSubmit}
      />
    </div>
  );

  const resultsPage = loading ? <LoadingPage /> : <ResultsPage results={results} />;

  return (
    <div className="App">
      <nav className="App-nav">
        <div>Git Scrape</div>
        <div>View on Github</div>
      </nav>
      {results ? resultsPage : searchPage}
    </div>
  );
}

export default App;
